import React, {useState} from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart as faSolidHeart } from "@fortawesome/free-solid-svg-icons";
import { faHeart as faEmptyHeart } from "@fortawesome/free-regular-svg-icons";

const PostCard = ({postChild}) => {
    const [hover, setHover] = useState(false);

    if (postChild.length <= 0){
        return(<div></div>);
    }
    else {
        if (postChild.data.preview === undefined)
        {
            return(
            <article className="bg-red-100 m-5 overflow-hidden rounded-lg shadow-lg">
                

                <header className="bg-green-100 max-h-80 flex items-center justify-between leading-tight p-2 md:p-4">
                    <h1 className="text-lg max-h-52 overflow-hidden">
                        <a className="bg-purple-100 no-underline hover:underline text-black" href="#">
                            {postChild.data.title}
                        </a>
                    </h1>
                    <p className="flex items-end bg-red-300 text-grey-darker text-sm">
                        11/1/19
                    </p>
                </header>

                <footer className="flex items-center justify-between leading-none p-2 md:p-4">
                    <a className="flex flex-col bg-green-300 max-h-12 flex items-center no-underline hover:underline text-black">
                        <p className="ml-2 mb-2 text-sm font-semibold">
                            {"u/" + postChild.data.author}
                        </p>
                        <p className="ml-2 -mb-2 text-xs font-thin text-gray-800">
                            Subreddit Name
                        </p>
                    </a>
                        <a className="no-underline text-grey-darker hover:text-red-dark" href="/">
                        <span className="hidden">Like</span>
                        <FontAwesomeIcon icon={!hover ? faEmptyHeart : faSolidHeart} onPointerOver={() => {setHover(true);}} onPointerOut={() => {setHover(false);}}/>
                    </a>
                </footer>

            </article>
            );
        }
        else{
            return(
            <article className="m-5 overflow-hidden rounded-lg shadow-lg">
                <a href="#">
                    <img alt="Placeholder" className="block h-auto w-full" src="https://picsum.photos/600/400/?random"/>
                </a>

                <header className="flex items-center justify-between leading-tight p-2 md:p-4">
                    <h1 className="text-lg max-h-20 overflow-hidden">
                        <a className="no-underline hover:underline text-black" href="#">
                            {postChild.data.title}
                        </a>
                    </h1>
                    <p className="text-grey-darker text-sm">
                        11/1/19
                    </p>
                </header>

                <footer className="flex items-center justify-between leading-none p-2 md:p-4">
                    <a className="flex flex-col bg-green-300 max-h-12 flex items-center no-underline hover:underline text-black">
                        <p className="ml-2 mb-2 text-sm font-semibold">
                            {"u/" + postChild.data.author}
                        </p>
                        <p className="ml-2 -mb-2 text-xs font-thin text-gray-800">
                            Subreddit Name
                        </p>
                    </a>
                    <a className="no-underline text-grey-darker hover:text-red-dark">
                        <span className="hidden">Like</span>
                        <FontAwesomeIcon icon={!hover ? faEmptyHeart : faSolidHeart} onPointerOver={() => {setHover(true);}} onPointerOut={() => {setHover(false);}}/>
                    </a>
                </footer>

            </article>
            );
        }
    }
}

export default PostCard;
