import React, {useState} from 'react';

const SearchSubreddits = () => {
    const [query, setQuery] = useState();
    const baseURL = `https://www.reddit.com/subreddits/search.json?query=${query}&limit=10`;

    return(
        <div>
            <div className="bg-white flex items-center rounded-full shadow-xl">
            <input className="rounded-l-full w-full py-4 px-6 text-gray-700 leading-tight focus:outline-none" id="search" type="text" placeholder="Search"/>
                <div className="p-4">
                    <button className="bg-blue-500 text-white rounded-full p-2 hover:bg-blue-400 focus:outline-none w-12 h-12 flex items-center justify-center">
                        <svg href="https://www.flaticon.com/svg/vstatic/svg/49/49116.svg?token=exp=1615256208~hmac=8a5f6f867f849588eb8e673ff928c692"></svg>
                    </button>
                </div>
            </div>
        </div>
    );
};

export default SearchSubreddits;
