import React, { useEffect } from 'react';
import './App.css';

function App() {
  const xhr = new XMLHttpRequest();
  const subreddit = "memes"
  const url = `https://www.reddit.com/r/${subreddit}/top.json?limit=10`;
  const url1 = `https://www.reddit.com/api/v1/authorize?client_id=BmIWdmE0hYrM7Q&response_type=code&state=alskdjfhg&redirect_uri=http://localhost:3000&duration=permanent&scope=identity`;
  const url2 = `https://www.reddit.com/subreddits/search?q=red`;
  // var windowFeatures = "menubar=yes,location=yes,resizable=yes,scrollbars=yes,status=yes";

  // window.addEventListener('message', event => {console.log(event);}, false);

  // var authWin = window.open(url1, "Authentication Popup", "location,resizable,left=400,top=275,width=700,height=400")

  fetch(url2, {
    method: 'GET',
    mode: 'cors',
    credentials: 'include',
    headers:{
      'Access-Control-Allow-Origin': 'https://www.reddit.com',
      'Test-Custom-Header': 'Testing',
      'Access-Control-Allow-Credentials': 'true',
    }
  })
  .then((res) => {
    console.log(res);
  })
  .catch((error) => {
    console.log("***ERROR: " + error);
  })


  return (
    <div className="App">
      <p>{url1}</p>
    </div>
  );
}

export default App;
