import NavBar from "./nav.js";

const layout = (props) => {
  return (
    <div className="relative min-h-screen bg-purple-100">
      <NavBar />
      <main className="pb-32 mx-auto sm:max-w-screen-lg">{props.children}</main>
    </div>
  );
};

export default layout;
