import React, {useState, useEffect} from 'react';
import searchIcon from '../Images/favicon-32x32.png';

const SearchSubreddits = () => {
    const [query, setQuery] = useState("");
    const baseURL = `https://www.reddit.com/subreddits/search.json?query=${query}&limit=10`;

    useEffect(() =>{
        console.log("effect: " + query);
    })

    const changeValueHandler = (e) => {
        console.log("state: " + query);
        setQuery(e.target.value);

        console.log(e.target.value);
        console.log(query);

        window.fetch(baseURL)
        .then((res) => {
            console.log(res.json());
        })
    }

    return(
        <div>
            <div className="bg-white flex items-center rounded-full shadow-xl">
            <input 
            className="rounded-l-full w-full py-4 px-6 text-gray-700 leading-tight focus:outline-none" 
            id="search" 
            type="text" 
            placeholder="Search"
            value={query}
            onChange={changeValueHandler}
            />
                <div className="p-4">
                    <button className="bg-blue-500 text-white rounded-full p-2 hover:bg-blue-400 focus:outline-none w-12 h-12 flex items-center justify-center">
                        <link rel="icon" type="image/png" href="../Images/favicon-32x32.png"/>
                    </button>
                </div>
            </div>
            <textarea value={query}/>
        </div>
    );
};

export default SearchSubreddits;
