import React, {useState, useEffect} from 'react';
import searchIcon from '../Images/favicon-32x32.png';

const SearchSubreddits = () => {
    const [query, setQuery] = useState("");
    const [searchResults, setSearchResults] = useState([]);
    const baseURL = `https://www.reddit.com/subreddits/search.json?q=${query}&limit=10`;

    useEffect(() =>{
        fetch(baseURL)
        .then((res) => {
            res.json().then((data) => {
                if (data.data !== undefined){
                    setSearchResults(data.data.children);
                    console.log(searchResults);
                }
            })
        })
        .catch((error) => {
            console.log("***ERROR: " + error);
        })
    })

    const changeValueHandler = (e) => {
        setQuery(e.target.value);
    }

    return(
        <div>
            <div className="bg-white flex items-center rounded-full shadow-xl">
            <input 
            className="rounded-l-full w-full py-4 px-6 text-gray-700 leading-tight focus:outline-none" 
            id="search" 
            type="text" 
            placeholder="Search"
            value={query}
            onChange={changeValueHandler}
            />
                <div className="p-4">
                    <button className="bg-blue-500 text-white rounded-full p-2 hover:bg-blue-400 focus:outline-none w-12 h-12 flex items-center justify-center">
                        <link rel="icon" type="image/png" href="../Images/favicon-32x32.png"/>
                    </button>
                </div>
            </div>
            <div 
            className="origin-top-center relative right-0 mt-2 w-3/6 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 focus:outline-none" 
            role="menu" 
            aria-orientation="vertical" 
            aria-labelledby="options-menu"
            >
                <div className="py-1" role="none">
                    <a href="#" 
                    className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900" 
                    role="menuitem"
                    >Item 1</a>
                    <a href="#" 
                    className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900" 
                    role="menuitem"
                    >Item 2</a>
                    <a href="#" 
                    className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900" 
                    role="menuitem"
                    >Item 3</a>
                </div>
            </div>
        </div>
    );
};

export default SearchSubreddits;
