import React, {useState, useEffect} from 'react';
import { SearchResDropDown} from './searchResultDropdown.js';
import PostCard from './postCard.js';

const SearchSubreddits = () => {
    const [query, setQuery] = useState("");
    const [search, setSearch] = useState("");
    const [searchResults, setSearchResults] = useState([]);
    const baseURL = `https://www.reddit.com/subreddits/search.json?q=${query}&limit=10`;

    // Consistently update query string to match what is written
    useEffect(() =>{
        setQuery(search);
    })

    // Update when the query string has been updated and fetch the search results
    useEffect(() => {
        fetch(baseURL)
        .then((res) => {
            res.json().then((data) => {
                if (data.data !== undefined){
                    setSearchResults(data.data.children);
                    console.log(searchResults);
                }
            })
        })
        .catch((error) => {
            console.log("***ERROR: " + error);
        })
    }, [query])

    // Update the value of the searchbar search string
    const changeValueHandler = (e) => {
        setSearch(e.target.value);
    }

    return(
        <div>
            <div className="bg-white flex items-center rounded-full shadow-xl">
            <input 
            className="rounded-l-full w-full py-4 px-6 text-gray-700 leading-tight focus:outline-none" 
            id="search" 
            type="text" 
            placeholder="Search"
            value={search}
            onChange={changeValueHandler}
            />
                <div className="p-4">
                    <button className="bg-blue-500 text-white rounded-full p-2 hover:bg-blue-400 focus:outline-none w-12 h-12 flex items-center justify-center">
                        <link rel="icon" type="image/png" href="../Images/favicon-32x32.png"/>
                    </button>
                </div>
            </div>

            <div 
            className="origin-top-right absolute left-0 ml-10 mt-0.5 w-3/6 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 focus:outline-none" 
            role="menu" 
            aria-orientation="vertical" 
            aria-labelledby="options-menu"
            >
                <SearchResDropDown 
                result={searchResults} 
                search={search}
                setQuery={setQuery} 
                setSearch={setSearch}
                />
            </div>

            <div class="container my-12 mx-auto px-4 md:px-12">
                <div class="flex flex-wrap -mx-1 lg:-mx-4">
                    <div class="my-1 px-1 w-full md:w-1/2 lg:my-4 lg:px-4 lg:w-3/6">
                        <PostCard/>
                        <PostCard/>
                        <PostCard/>
                        <PostCard/>
                        <PostCard/>
                        <PostCard/>
                        <PostCard/>

                    </div>
                </div>
            </div>
        </div>
    );
};

export default SearchSubreddits;
