import React, {useState} from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart as faSolidHeart } from "@fortawesome/free-solid-svg-icons";
import { faHeart as faEmptyHeart } from "@fortawesome/free-regular-svg-icons";

const PostCard = ({postChild}) => {
    const [hover, setHover] = useState(false);

    const RenderMedia = () => {
        if (postChild.data.secure_media !== null){
            console.log(postChild.data.secure_media);
            if (postChild.data.secure_media.oembed !== undefined)
            {
                let htmlString = postChild.data.secure_media.oembed.html;

                htmlString.replace("&lt;", "<");
                htmlString.replace("&gt;", ">");

                return <div className="block max-h-96 w-full" dangerouslySetInnerHTML={{__html: htmlString}} />;
            }
            else{
                return(
                    <iframe className="block max-h-96 w-full" height={postChild.data.secure_media.reddit_video.height} width={postChild.data.secure_media.reddit_video.width} src={postChild.data.secure_media.reddit_video.fallback_url}/>
                );
            }
            return (<div></div>)
        }
        else{
            return (
                <a href={"https://www.reddit.com" + postChild.data.permalink}>
                    <img alt="Placeholder" className="block h-auto w-full" src={postChild.data.url}/>
                </a>
            );
        }
    }

    if (postChild.length <= 0){
        return(<div></div>);
    }
    else {
        if (postChild.data.preview === undefined)
        {
            return(
            <article className="m-5 overflow-hidden rounded-lg shadow-lg">
                <header className="max-h-80 flex items-center justify-between leading-tight p-2 md:p-4">
                    <h1 className="text-lg max-h-52 overflow-hidden">
                        <a className="no-underline hover:underline text-black" href={"https://www.reddit.com" + postChild.data.permalink}>
                            {postChild.data.title}
                        </a>
                    </h1>
                    <p className="flex items-end text-grey-darker text-sm">
                        {(postChild.data.score % 1000) + "K"}
                    </p>
                </header>

                <footer className="flex items-center justify-between leading-none p-2 md:p-4">
                    <a className="flex flex-col max-h-12 flex items-center no-underline hover:underline text-black">
                        <p className="ml-2 mb-2 text-sm font-semibold">
                            {"u/" + postChild.data.author}
                        </p>
                        <p className="ml-2 -mb-2 text-xs font-thin text-gray-800">
                            {postChild.data.subreddit_name_prefixed}
                        </p>
                    </a>
                        <a className="no-underline text-grey-darker hover:text-red-dark">
                        <span className="hidden">Like</span>
                        <FontAwesomeIcon icon={!hover ? faEmptyHeart : faSolidHeart} onPointerOver={() => {setHover(true);}} onPointerOut={() => {setHover(false);}}/>
                    </a>
                </footer>

            </article>
            );
        }
        else{
            return(
            <article className="m-5 overflow-hidden rounded-lg shadow-lg">
                <RenderMedia />
                <link rel="canonical" href="https://getbootstrap.com/docs/4.0/examples/blog/"/>

                <header className="flex items-center justify-between leading-tight p-2 md:p-4">
                    <h1 className="text-lg max-h-20 overflow-hidden">
                        <a className="no-underline hover:underline text-black" href={"https://www.reddit.com" + postChild.data.permalink}>
                            {postChild.data.title}
                        </a>
                    </h1>
                    <p className="text-grey-darker text-sm">
                        {(postChild.data.score % 1000) + "K"}
                    </p>
                </header>

                <footer className="flex items-center justify-between leading-none p-2 md:p-4">
                    <a className="flex flex-col max-h-12 flex items-center no-underline hover:underline text-black">
                        <p className="ml-2 mb-2 text-sm font-semibold">
                            {"u/" + postChild.data.author}
                        </p>
                        <p className="ml-2 -mb-2 text-xs font-thin text-gray-800">
                            {postChild.data.subreddit_name_prefixed}
                        </p>
                    </a>
                    <a className="no-underline text-grey-darker hover:text-red-dark">
                        <span className="hidden">Like</span>
                        <FontAwesomeIcon icon={!hover ? faEmptyHeart : faSolidHeart} onPointerOver={() => {setHover(true);}} onPointerOut={() => {setHover(false);}}/>
                    </a>
                </footer>

            </article>
            );
        }
    }
}

export default PostCard;
